import React, { useState, useEffect } from 'react';
import { useCart } from '../CartContext'; // Adjust the path as per your project structure
import './Home.css'; // Ensure this CSS file is imported if you added the styles there

const Home = () => {
  const [products, setProducts] = useState([]);
  const { addToCart } = useCart(); // Destructure addToCart from useCart
  const [showNote, setShowNote] = useState(true);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch('http://127.0.0.1:5000/api/v1/products');
        const data = await response.json();
        setProducts(data.products);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();

    // Hide the welcome note after a few seconds
    const timer = setTimeout(() => {
      setShowNote(false);
    }, 5000); // Change duration as needed

    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="home">
      <div className={`sliding-note ${showNote ? 'active' : ''}`}>
        Welcome to Our Store! Check out our latest products below.
      </div>

        {products.map((product) => (
          <div key={product.id} style={{ border: '1px solid rgb(214, 154, 91)', width: '30%', marginRight: '0.5rem', marginBottom: '0.5rem', borderRadius: '0.5rem', marginTop: '0.5rem', textAlign: 'center', display: 'flex', flexDirection: 'column', padding: '1rem' }}>
            <img src={product.image} alt={product.name} style={{ width: 'calc(100% - 20px)', height: 'auto', borderRadius: '0.5rem', marginBottom: '1rem' }} />
            <div style={{ padding: '1rem', border: '1px solid #ccc', borderTop: 'none', borderRadius: '0 0 0.5rem 0.5rem', backgroundColor: 'rgb(205, 152, 102)', color: 'white', display: 'flex', flexDirection: 'column', justifyContent: 'space-between' }}>
              <h2>{product.name}</h2>
              <p>Price: {Math.round(product.price)}</p>
              <p>Stock: {product.stock}</p>
              <button style={{ width: '100%', padding: '0.75rem', backgroundColor: '#ca8d5b', color: 'white', border: 'none', borderRadius: '0.25rem', cursor: 'pointer' }} onClick={() => addToCart(product)}>Add to Cart</button>
            </div>
          </div>
        ))}
      </div>
    
  );
};

export default Home;
