// src/Component/Pages/Cart.js
import React, { useContext } from 'react';
import { useCart } from '../CartContext';
import { AuthContext } from '../AuthContext'; // Import AuthContext for user authentication
import './Cart.css';

const Cart = () => {
  const { cartItems, setCartItems } = useCart();
  const { user } = useContext(AuthContext); // Get the authenticated user from context

  const totalCost = cartItems.reduce((sum, item) => sum + item.price, 0);

  const handleOrder = async () => {
    if (!user) {
      alert('Please log in to place an order.');
      return;
    }

    try {
      const response = await fetch('http://127.0.0.1:5000/api/v1/orders', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${user.token}` // Assuming you have a token-based authentication
        },
        body: JSON.stringify({
          userId: user.id,
          items: cartItems
        })
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Order placed:', data.orders);
        setCartItems([]);
      } else {
        const errorData = await response.json();
        console.error('Failed to place order', errorData);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="cart">
      <h1>Your Cart</h1>
      <div className="cart-items">
        {cartItems.length ? (
          cartItems.map((item) => (
            <div key={item.id} className="cart-item">
              <span>{item.name}</span>
              <span>Price: ${item.price}</span>
            </div>
          ))
        ) : (
          <div>No items in the cart</div>
        )}
      </div>
      {cartItems.length > 0 && (
        <div>
          <div className="total-cost">
            <strong>Total Cost: ${totalCost.toFixed(2)}</strong>
          </div>
          <button className="order-button" onClick={handleOrder}>
            Place Order
          </button>
        </div>
      )}
    </div>
  );
};

export default Cart;
