// Cart.js
import React, { useContext } from 'react';
import { useCart } from '../CartContext';
import { AuthContext } from '../'; // Assuming AuthContext is defined in AuthContext.js

const Cart = () => {
  const { cartItems, totalPrice } = useCart();
  const { user } = useContext(AuthContext); // Using useContext to access AuthContext

  const createOrder = async () => {
    // Ensure user is logged in before proceeding
    if (!user) {
      console.log('User not logged in. Redirecting to login page...');
      // Implement redirection logic to login page or show a message
      return;
    }

    // Collect client details (dummy data for illustration)
    const clientDetails = {
      name: user.name, // Assuming user object has a 'name' property
      address: user.address // Assuming user object has an 'address' property
      // Add more client details as needed
    };

    // Prepare order data
    const orderData = {
      client: clientDetails,
      cartItems: cartItems,
      totalPrice: totalPrice
    };

    try {
      // Send order data to backend API
      const response = await fetch('http://127.0.0.1:5000/api/v1/orders', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(orderData)
      });

      if (response.ok) {
        // Order successfully created, now send email notification
        const emailResponse = await fetch('http://127.0.0.1:5000/api/v1/send-email', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(orderData)
        });

        if (emailResponse.ok) {
          console.log('Email notification sent successfully.');
        } else {
          console.error('Failed to send email notification:', emailResponse.statusText);
        }
        
        // Clear cart items or update state as needed
      } else {
        // Handle error response
        console.error('Failed to create order:', response.statusText);
      }
    } catch (error) {
      console.error('Error creating order:', error);
    }
  };

  return (
    <div className="cart">
      <h2>Cart</h2>
      <ul>
        {cartItems.map((item, index) => (
          <li key={index}>{item.name} - ${item.price}</li>
        ))}
      </ul>
      <p>Total Price: ${totalPrice}</p>
      <button onClick={createOrder}>Order</button>
    </div>
  );
};

export default Cart;
