// Login.js
import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

const Login = ({ isAdmin }) => { // Pass isAdmin as a prop
  const [credentials, setCredentials] = useState({
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const history = useHistory();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredentials({
      ...credentials,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const endpoint = isAdmin 
      ? 'http://127.0.0.1:5000/api/v1/admin/login' 
      : 'http://127.0.0.1:5000/api/v1/user/login'; // Adjust the API endpoint

    try {
      const response = await fetch(endpoint, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(credentials)
      });
      const data = await response.json();

      if (response.ok) {
        const tokenKey = isAdmin ? 'adminToken' : 'token'; // Use different tokens
        localStorage.setItem(tokenKey, data.token); // Store token in localStorage
        localStorage.setItem('isAdmin', isAdmin ? 'true' : 'false'); // Store user role
        
        if (isAdmin) {
          history.push('/admin-dashboard'); // Redirect to admin dashboard
        } else {
          history.push('/products'); // Redirect to products page
        }
      } else {
        setError(data.message || 'Failed to login');
      }
    } catch (error) {
      setError('An error occurred. Please try again.');
    }
  };

  return (
    <div className="login-container">
      <h2>{isAdmin ? 'Admin Login' : 'Customer Login'}</h2>
      {error && <p>{error}</p>}
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Email:</label>
          <input 
            type="email" 
            name="email" 
            value={credentials.email} 
            onChange={handleChange} 
            required 
          />
        </div>
        <div className="form-group">
          <label>Password:</label>
          <input 
            type="password" 
            name="password" 
            value={credentials.password} 
            onChange={handleChange} 
            required 
          />
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
