import React from 'react';
import logo from './logo_image.jpg';
import { BrowserRouter as Router, Switch, Route, Redirect } from 'react-router-dom';
import Navbar from './Component/Navbar';
import Home from './Component/Pages/Home';
import CreateAccount from './Component/Pages/CreateAccount';
import Order from './Component/Pages/Order';
import Cart from './Component/Pages/Cart';
import AdminDashboard from './Component/Pages/AdminDashboard';
import Login from './Component/Pages/login'; // Import Login component
import Footer from './Component/Footer';

import './App.css';

function App() {
  const handleSearch = () => {
    console.log('Perform search');
  };

  const isAuthenticated = !!localStorage.getItem('token'); // Check if the token exists

  return (
    <div className="App">
      <header className="App-header">
        <div className="left-section">
          <img src={logo} className="App-logo" alt="logo" />
        </div>

        <div className="search-bar">
          <input className="active" type="text" placeholder="Search..." />
          <button onClick={handleSearch}>Search</button>
        </div>
      </header>

      <Router>
        <div> 
          <Navbar />
          <Switch>
            <Route path="/home">
              <Home />
            </Route>
            <Route path="/cart">
              <Cart />
            </Route>
            <Route path="/create-account">
              <CreateAccount />
            </Route>
            <Route path="/login">
              <Login />
            </Route>
            <PrivateRoute path="/order" component={Order} isAuthenticated={isAuthenticated} />
            <PrivateRoute path="/admin-dashboard" component={AdminDashboard} isAuthenticated={isAuthenticated} />
            <Redirect from="/" to="/home" />
          </Switch>
          <Footer />
        </div>
      </Router>
    </div>
  );
}

// Private Route component to protect routes
const PrivateRoute = ({ component: Component, isAuthenticated, ...rest }) => (
  <Route
    {...rest}
    render={(props) =>
      isAuthenticated ? <Component {...props} /> : <Redirect to="/login" />
    }
  />
);

export default App;
