import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';

const AdminDashboard = () => {
  const [orders, setOrders] = useState([]);
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    name: '',
    price: '',
    stock: '',
    image: ''
  });
  const [error, setError] = useState('');
  const history = useHistory();

  useEffect(() => {
    fetchOrders();
    fetchProducts();
  }, []);
  const handleApproveOrder = async (orderId) => {
    const token = localStorage.getItem('adminToken');
    try {
      const response = await fetch(`http://127.0.0.1:5000/api/v1/orders/${orderId}/approve`, {
        method: 'PUT', // Assuming you use PUT for updating order status
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });
  
      if (response.ok) {
        // Optionally, you can refresh the orders list after approval
        fetchOrders();
      } else {
        setError('Failed to approve order');
      }
    } catch (error) {
      setError('Failed to approve order');
    }
  };

  const fetchOrders = async () => {
    const token = localStorage.getItem('adminToken');
    try {
      const response = await fetch('http://127.0.0.1:5000/api/v1/orders', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });
      const data = await response.json();
      setOrders(data.orders);
    } catch (error) {
      setError('Failed to fetch orders');
    }
  };

  const fetchProducts = async () => {
    const token = localStorage.getItem('adminToken');
    try {
      const response = await fetch('http://127.0.0.1:5000/api/v1/products', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });
      const data = await response.json();
      setProducts(data.products);
    } catch (error) {
      setError('Failed to fetch products');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct((prevProduct) => ({
      ...prevProduct,
      [name]: value
    }));
  };

  const handleAddProduct = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('adminToken');
    try {
      const response = await fetch('http://127.0.0.1:5000/api/v1/products', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify(newProduct)
      });

      if (response.ok) {
        fetchProducts(); // Refresh the product list
        setNewProduct({
          name: '',
          price: '',
          stock: '',
          image: ''
        }); // Reset the form
      } else {
        setError('Failed to add product');
      }
    } catch (error) {
      setError('Failed to add product');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('adminToken');
    history.push('/login');
  };

  return (
    <div className="admin-dashboard">
      <h2>Admin Dashboard</h2>
      <button onClick={handleLogout}>Logout</button>
      {error && <div className="error-message">{error}</div>}
      
      <div className="section">
        <h3>Orders</h3>
        <ul>
          {orders.map((order) => (
            <li key={order.id}>
              Order ID: {order.id}, Customer: {order.customer_name}, Total: {order.total}
              <button onClick={() => handleApproveOrder(order.id)}>Approve</button>
            </li>
          ))}
        </ul>
      </div>

      <div className="section">
        <h3>Products</h3>
        <ul>
          {products.map((product) => (
            <li key={product.id}>
              Name: {product.name}, Price: {product.price}, Stock: {product.stock}
            </li>
          ))}
        </ul>
      </div>

      <div className="section">
        <h3>Add Product</h3>
        <form onSubmit={handleAddProduct} className="add-product-form">
          <div className="form-group">
            <label>Name:</label>
            <input
              type="text"
              name="name"
              value={newProduct.name}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Price:</label>
            <input
              type="number"
              name="price"
              value={newProduct.price}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Stock:</label>
            <input
              type="number"
              name="stock"
              value={newProduct.stock}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Image URL:</label>
            <input
              type="text"
              name="image"
              value={newProduct.image}
              onChange={handleInputChange}
              required
            />
          </div>
          <button type="submit" className="btn btn-success">Add Product</button>
        </form>
      </div>
    </div>
  );
};

export default AdminDashboard;
