import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Switch, Route, Redirect } from 'react-router-dom';
import Navbar from './Component/Navbar';
import Home from './Component/Pages/Home';
import CreateAccount from './Component/Pages/CreateAccount';
import Order from './Component/Pages/Order';
import Cart from './Component/Pages/Cart';
import AdminDashboard from './Component/Pages/AdminDashboard'; // Correct casing
import Login from './Component/Pages/Login'; // Correct casing
import Footer from './Component/Footer';
import { CartProvider } from './Component/CartContext'; // Adjust the path as per your project structure
import { AuthProvider } from './Component/auth'; // Add AuthProvider import
import SearchBar from './Component/SearchBar'; // Import SearchBar component
import './App.css';
import logo from './fierce__-removebg-preview.png';

function App() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      const response = await fetch('/api/products'); // Adjust the URL as needed
      if (response.ok) {
        const data = await response.json();
        setProducts(data);
      } else {
        console.error("Failed to fetch products");
      }
    };

    fetchProducts();
  }, []);

  // Check for admin status from localStorage or other source
  const isAdmin = localStorage.getItem('isAdmin') === 'true';

  return (
    <AuthProvider>
      <CartProvider>
        <div className="App">
          <header className="App-header">
            <div className="left-section">
              <img src={logo} className="App-logo" alt="logo" />
            </div>
            <SearchBar products={products} />
          </header>

          <Router>
            <div>
              <Navbar />
              <Switch>
                <Route path="/home" exact>
                  <Home />
                </Route>
                <Route path="/cart" exact>
                  <Cart />
                </Route>
                <Route path="/create-account" exact>
                  <CreateAccount />
                </Route>
                <Route path="/order" exact>
                  <Order />
                </Route>
                <Route path="/login" exact>
                  <Login />
                </Route>
                <Route path="/admin/login" exact>
                  {/* Implement AdminLogin component if you have one */}
                  <AdminLogin />
                </Route>
                {isAdmin ? (
                  <Route path="/admin-dashboard" exact>
                    <AdminDashboard />
                  </Route>
                ) : (
                  <Redirect from="/admin-dashboard" to="/home" />
                )}
                <Redirect from="/" to={isAdmin ? "/admin-dashboard" : "/home"} />
                <Route path="*">
                  <Redirect to={isAdmin ? "/admin-dashboard" : "/home"} />
                </Route>
              </Switch>
              <Footer />
            </div>
          </Router>
        </div>
      </CartProvider>
    </AuthProvider>
  );
}

export default App;
