// src/Component/Pages/Order.js
import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '.'; // Import AuthContext for user authentication
import './Order.css';

const Order = () => {
  const { user } = useContext(AuthContext); // Get the authenticated user from context
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    if (!user) {
      return;
    }

    const fetchOrders = async () => {
      try {
        const response = await fetch(`http://127.0.0.1:5000/api/v1/orders?userId=${user.id}`, {
          headers: {
            'Authorization': `Bearer ${user.token}`
          }
        });
        const data = await response.json();
        setOrders(data.orders);
      } catch (error) {
        console.error('Error fetching orders:', error);
      }
    };

    fetchOrders();
  }, [user]);

  return (
    <div className="orders">
      <h1>Your Orders</h1>
      <div className="orders-list">
        {orders.length ? (
          orders.map((order) => (
            <div key={order.id} className="order-item">
              <div>Order ID: {order.id}</div>
              <div>Total Price: ${order.total_price}</div>
              <div>Status: {order.status}</div>
              <div>Created At: {new Date(order.created_at).toLocaleString()}</div>
              <div>Updated At: {new Date(order.updated_at).toLocaleString()}</div>
              <div>
                <strong>Items:</strong>
                <ul>
                  <li>Product ID: {order.product_id} - Quantity: {order.quantity}</li>
                </ul>
              </div>
            </div>
          ))
        ) : (
          <div>No orders found</div>
        )}
      </div>
    </div>
  );
};

export default Order;
