// src/Component/Pages/Product.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Product.css';
import cartLogo from '../cart.jpg'; // Assuming you have a cart logo image
import { useCart } from './'; // Import useCart hook

export function MiniProduct({ product = {} }) {
  const { id, name, price, stock, thumbnail } = product;
  const { addToCart } = useCart(); // Use the addToCart function from context

  const handleAddToCart = () => {
    addToCart(product);
  };

  return (
    <div id={id} className='product-card'>
      <div className='description-and-thumbnail'>
        <img src={thumbnail} alt='thumbnail' width='80px' height='80px' />
      </div>
      <div className='product-details padded-box'>
        <div className='product-info'>
          <div>
            <label>Name:</label>
            <span>{name}</span>
          </div>
          <div>
            <label>Price:</label>
            <span>{price}</span>
          </div>
          <div>
            <label>Stock:</label>
            <span>{stock}</span>
          </div>
        </div>
        <div className='add-to-cart'>
          <button onClick={handleAddToCart}>
            <img src={cartLogo} alt='Add to cart' className='cart-logo' />
            Add to cart
          </button>
        </div>
      </div>
    </div>
  );
}

export function Products() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch('https://dummyjson.com/products')
      .then(res => res.json())
      .then(data => setProducts(data.products));
  }, []);

  return (
    <div className='products-section'>
      <h3>Products on sale</h3>
      <div className='products-list'>
        {products.length ? products.map(product => <MiniProduct product={product} key={product.id} />) : <div>No products available</div>}
      </div>
      <div className='add-product-link padded-box'>
        <Link to='/products/add' role='button' className='btn btn-success'>Add a product</Link>
      </div>
    </div>
  );
}

export default Products;
