import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import './Product.css';

export function Products() {
    const [products, setProducts] = useState([]);
    const [error, setError] = useState('');
    const [searchTerm, setSearchTerm] = useState('');

    const fetchProducts = useCallback(async () => {
        try {
            let url = 'http://127.0.0.1:5000/api/v1/products';
            if (searchTerm) {
                url = `http://127.0.0.1:5000/api/v1/products/search?name=${encodeURIComponent(searchTerm)}`;
            }

            const response = await fetch(url);
            if (!response.ok) {
                throw new Error(`Failed to fetch products: ${response.status} ${response.statusText}`);
            }
            const data = await response.json();
            setProducts(data.products);
        } catch (error) {
            console.error('Fetch error:', error);
            setError(error.message);
        }
    }, [searchTerm]);

    useEffect(() => {
        fetchProducts();
    }, [fetchProducts]);



    return (
        <div className='products-section'>
            <h3>Products on sale</h3>
            {error && <div className='error-message'>{error}</div>}
            <form onSubmit={handleSearchSubmit}>
                <input
                    type='text'
                    placeholder='Search by product name'
                    value={searchTerm}
                    onChange={handleSearchChange}
                />
                <button type='submit'>Search</button>
            </form>
            <div className='products-list'>
                {products.length ? (
                    products.map(product => (
                        <div key={product.id} className='product-card'>
                            <div className='description-and-thumbnail'>
                                <img src={product.image} alt='Product' width='80px' height='80px' />
                            </div>
                            <div className='product-details padded-box'>
                                <div className='product-info'>
                                    <div>
                                        <label>Name:</label>
                                        <span>{product.name}</span>
                                    </div>
                                    <div>
                                        <label>Price:</label>
                                        <span>{product.price}</span>
                                    </div>
                                    <div>
                                        <label>Stock:</label>
                                        <span>{product.stock}</span>
                                    </div>
                                </div>
                                <div className='add-to-cart'>
                                    <Link to={`/product/${product.id}`} className='btn btn-primary'>
                                        View Details
                                    </Link>
                                </div>
                            </div>
                        </div>
                    ))
                ) : (
                    <div>No products available</div>
                )}
            </div>
            <div className='add-product-link padded-box'>
                <Link to='/products/add' role='button' className='btn btn-success'>
                    Add a product
                </Link>
            </div>
        </div>
    );
}

export default Products;
