import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Product.css';
import cartLogo from '../cart.jpg'; // Assuming you have a cart logo image

export function MiniProduct({ product = {} }) {
  const { id, name, price, stock, thumbnail } = product;

  const handleAddToCart = () => {
    console.log('Added to cart:', product);
  };

  return (
    <div id={id} className='product-card'>
      <div className='description-and-thumbnail'>
        <img src={thumbnail} alt='thumbnail' width='80px' height='80px' />
      </div>
      <div className='product-details padded-box'>
        <div className='product-info'>
          <div>
            <label>Name:</label>
            <span>{name}</span>
          </div>
          <div>
            <label>Price:</label>
            <span>{price}</span>
          </div>
          <div>
            <label>Stock:</label>
            <span>{stock}</span>
          </div>
        </div>
        <div className='add-to-cart'>
          <button onClick={handleAddToCart}>
            <img src={cartLogo} alt='Add to cart' className='cart-logo' />
            Add to cart
          </button>
        </div>
      </div>
    </div>
  );
}

export function Products() {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const token = localStorage.getItem('token'); // Get the token from local storage
        const response = await fetch('http://127.0.0.1:5000/api/v1/products/', {
          headers: {
            'Authorization': `Bearer ${token}`, // Include the token in the request headers
          },
        });
        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(errorData.error || `Failed to fetch products: ${response.status} ${response.statusText}`);
        }
        const data = await response.json();
        setProducts(data.products);
      } catch (error) {
        console.error('Fetch error:', error);
        setError(error.message);
      }
    };

    fetchProducts();
  }, []);

  return (
    <div className='products-section'>
      <h3>Products on sale</h3>
      {error && <div className='error-message'>{error}</div>}
      <div className='products-list'>
        {products.length ? (
          products.map(product => <MiniProduct product={product} key={product.id} />)
        ) : (
          <div>No products available</div>
        )}
      </div>
      <div className='add-product-link padded-box'>
        <Link to='/products/add' role='button' className='btn btn-success'>
          Add a product
        </Link>
      </div>
    </div>
  );
}

export default Products;
