import React, { useState } from 'react';
import { useAuth } from '../'; // Import useAuth hook
import './CreateAccount.css';

const CreateAccount = () => {
  const { login } = useAuth(); // Destructure login from useAuth

  const [form, setForm] = useState({
    name: '',
    email: '',
    password: '',
    role: 'customer', // Default role
    phoneNumber: '',
    address: '',
  });

  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({
      ...form,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await login(form.email, form.password); // Call login function from context
      setMessage('Login successful!'); // Example message
      // Reset form or redirect user as needed after login
    } catch (error) {
      console.error('Login error:', error);
      setMessage('Failed to login. Please try again.'); // Example message
    }
  };

  return (
    <div className="outer-container">
      <div className="inner-box create-account">
        <h2>Login</h2>
        {message && <p>{message}</p>}
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="email">Email:</label>
            <input type="email" id="email" name="email" value={form.email} onChange={handleChange} required />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input type="password" id="password" name="password" value={form.password} onChange={handleChange} required />
          </div>
          <button type="submit">Login</button>
        </form>
      </div>
    </div>
  );
};

export default CreateAccount;
