// src/Component/Pages/CreateAccount.js
import React, { useState, useContext } from 'react';
import { AuthContext } from '../'; // Adjust the path as needed
import './CreateAccount.css';

const CreateAccount = () => {
  const [form, setForm] = useState({
    name: '',
    email: '',
    password: '',
    role: 'customer', // Default role
    phoneNumber: '',
    address: '',
  });

  const [message, setMessage] = useState('');
  const { login } = useContext(AuthContext); // Access the login function from AuthContext

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({
      ...form,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(form),
    };

    try {
      const response = await fetch('http://127.0.0.1:5000/api/v1/user/register', requestOptions);
      const data = await response.json();
      
      if (response.ok) {
        setMessage('Account created successfully');
        setForm({
          name: '',
          email: '',
          password: '',
          role: 'customer',
          phoneNumber: '',
          address: '',
        });
        // Automatically log in the user after successful account creation
        login(form.email, form.password);
      } else {
        setMessage(data.message || 'Failed to create account');
      }
    } catch (error) {
      console.error('Error:', error);
      setMessage('An error occurred. Please try again.');
    }
  };

  return (
    <div className="outer-container">
      <div className="inner-box create-account">
        <h2>Create Account</h2>
        {message && <p>{message}</p>}
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="name">Name:</label>
            <input type="text" id="name" name="name" value={form.name} onChange={handleChange} required />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email:</label>
            <input type="email" id="email" name="email" value={form.email} onChange={handleChange} required />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input type="password" id="password" name="password" value={form.password} onChange={handleChange} required />
          </div>
          <div className="form-group">
            <label htmlFor="role">Role:</label>
            <input type="text" id="role" name="role" value={form.role} onChange={handleChange} required />
          </div>
          <div className="form-group">
            <label htmlFor="phoneNumber">Phone Number:</label>
            <input type="text" id="phoneNumber" name="phoneNumber" value={form.phoneNumber} onChange={handleChange} required />
          </div>
          <div className="form-group">
            <label htmlFor="address">Address:</label>
            <input type="text" id="address" name="address" value={form.address} onChange={handleChange} required />
          </div>
          <button type="submit">Create Account</button>
        </form>
      </div>
    </div>
  );
};

export default CreateAccount;
