import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import "./adminDashboard.css"; // Import your custom CSS

const AdminDashboard = () => {
  const [orders, setOrders] = useState([]);
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    name: "",
    price: "",
    stock: "",
    image: "",
  });
  const [error, setError] = useState("");
  const history = useHistory();

  // Fetch both products and orders when the component mounts
  useEffect(() => {
    fetchProducts();
    fetchOrders();
  }, []);

  // Fetch all products from the API
  const fetchProducts = async () => {
    const token = localStorage.getItem("token"); // Use common token

    if (!token) {
      setError("No token found. Please log in.");
      return;
    }

    try {
      const response = await fetch("http://127.0.0.1:5000/api/v1/admin/products", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
      });

      if (response.ok) {
        const data = await response.json();
        setProducts(data.products || []);  // Fixed product fetch response
      } else {
        const errorData = await response.json();
        setError(errorData.message || "Failed to fetch products");
      }
    } catch (error) {
      console.error("Error fetching products:", error);
      setError("Error occurred while fetching products");
    }
  };

  // Fetch all orders from the API
  const fetchOrders = async () => {
    const token = localStorage.getItem("token");

    if (!token) {
      setError("No token found. Please log in.");
      return;
    }

    try {
      const response = await fetch("http://127.0.0.1:5000/api/v1/admin/orders", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
      });

      if (response.ok) {
        const data = await response.json();
        setOrders(data.orders || []);  // Fixed order fetch response
      } else {
        const errorData = await response.json();
        setError(errorData.message || "Failed to fetch orders");
      }
    } catch (error) {
      console.error("Error fetching orders:", error);
      setError("Error occurred while fetching orders");
    }
  };

  // Handle product form inputs
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct((prevProduct) => ({ ...prevProduct, [name]: value }));
  };

  // Handle adding a new product
  const handleAddProduct = async () => {
    const token = localStorage.getItem("token");

    if (!token) {
      setError("No token found. Please log in.");
      return;
    }

    try {
      const response = await fetch("http://127.0.0.1:5000/api/v1/admin/product", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(newProduct),
      });

      if (response.ok) {
        await fetchProducts();  // Refresh products after adding
        setNewProduct({ name: "", price: "", stock: "", image: "" }); // Reset form
      } else {
        const errorData = await response.json();
        setError(errorData.message || "Failed to add product");
      }
    } catch (error) {
      console.error("Error adding product:", error);
      setError("Error occurred while adding product");
    }
  };

  return (
    <div className="admin-dashboard">
      <h1>Admin Dashboard</h1>

      {/* Error display */}
      {error && <div className="error">{error}</div>}

      {/* Products Section */}
      <div className="products-section">
        <h2>Products</h2>
        <ul>
          {products.map((product) => (
            <li key={product.id}>
              {product.name} - ${product.price}
            </li>
          ))}
        </ul>
        {/* Add Product Form */}
        <h3>Add New Product</h3>
        <input
          type="text"
          name="name"
          value={newProduct.name}
          onChange={handleInputChange}
          placeholder="Product Name"
        />
        <input
          type="number"
          name="price"
          value={newProduct.price}
          onChange={handleInputChange}
          placeholder="Product Price"
        />
        <input
          type="number"
          name="stock"
          value={newProduct.stock}
          onChange={handleInputChange}
          placeholder="Product Stock"
        />
        <input
          type="text"
          name="image"
          value={newProduct.image}
          onChange={handleInputChange}
          placeholder="Product Image URL"
        />
        <button onClick={handleAddProduct}>Add Product</button>
      </div>

      {/* Orders Section */}
      <div className="orders-section">
        <h2>Orders</h2>
        <ul>
          {orders.map((order) => (
            <li key={order.id}>
              Order #{order.id} - Status: {order.status}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default AdminDashboard;
