import React, { useState } from 'react';
import logo from './logo_image.jpg';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Navbar from './Component/Navbar';
import Home from './Component/Pages/Home';
import Products from './Component/Pages/Products';
import Review from './Component/Pages/Review';
import CreateAccount from './Component/Pages/CreateAccount';
import Order from './Component/Pages/Order';
import Cart from './Component/Pages/Cart'; // Import Cart component
import './App.css';
import Footer from './Component/Footer';

function App() {
  const [cartItems, setCartItems] = useState([]);

  const handleAddToCart = (product) => {
    const updatedCart = [...cartItems, product];
    setCartItems(updatedCart);
    console.log('Added to cart:', product);
    console.log('Cart items:', updatedCart); // Verify cart items in console
  };

  const handleSearch = () => {
    console.log('Perform search');
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="left-section">
          <img src={logo} className="App-logo" alt="logo" />
        </div>

        <div className="search-bar">
          <input className="active" type="text" placeholder="Search..." />
          <button onClick={handleSearch}>Search</button>
        </div>
      </header>

      <Router>
        <div> 
          <Navbar />
          <Switch>
            <Route exact path="/">
              <Home />
            </Route>
            <Route path="/Product">
              <Products addToCart={handleAddToCart} /> {/* Pass addToCart function */}
            </Route>
            <Route path="/Review">
              <Review />
            </Route>
            <Route path="/Cart">
              <Cart cartItems={cartItems} />
            </Route>
            <Route path="/Create-account">
              <CreateAccount />
            </Route>
            <Route path="/Order">
              <Order />
            </Route>
          </Switch>
          <Footer/>
        </div>
      </Router>
    </div>
  );
}

export default App;
