// AdminDashboard.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import ''; // Create CSS file for styling if needed

function AdminDashboard() {
  const [orders, setOrders] = useState([]);
  const [products, setProducts] = useState([]);
  const [showAddProductForm, setShowAddProductForm] = useState(false);
  const [newProduct, setNewProduct] = useState({
    name: '',
    price: '',
    stock: '',
    image: ''
  });

  useEffect(() => {
    fetchOrders();
    fetchProducts();
  }, []);

  const fetchOrders = async () => {
    try {
      const response = await fetch('http://127.0.0.1:5000/api/v1/orders', {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      if (!response.ok) {
        throw new Error(`Failed to fetch orders: ${response.status} ${response.statusText}`);
      }
      const data = await response.json();
      setOrders(data.orders);
    } catch (error) {
      console.error('Fetch orders error:', error);
    }
  };

  const fetchProducts = async () => {
    try {
      const response = await fetch('http://127.0.0.1:5000/api/v1/products', {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      if (!response.ok) {
        throw new Error(`Failed to fetch products: ${response.status} ${response.statusText}`);
      }
      const data = await response.json();
      setProducts(data.products);
    } catch (error) {
      console.error('Fetch products error:', error);
    }
  };

  const handleAddProduct = async () => {
    // Implement functionality to add a product
    try {
      const response = await fetch('http://127.0.0.1:5000/api/v1/products', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
        body: JSON.stringify(newProduct),
      });
      if (!response.ok) {
        throw new Error(`Failed to add product: ${response.status} ${response.statusText}`);
      }
      const data = await response.json();
      console.log('Product added:', data.product);
      // Optionally update state or reset form fields after successful addition
      setNewProduct({
        name: '',
        price: '',
        stock: '',
        image: ''
      });
      fetchProducts(); // Refresh products list
    } catch (error) {
      console.error('Add product error:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct({
      ...newProduct,
      [name]: value
    });
  };

  const toggleAddProductForm = () => {
    setShowAddProductForm(!showAddProductForm);
  };

  return (
    <div className='admin-dashboard'>
      <h2>Admin Dashboard</h2>
      
      <div className='section'>
        <h3>Orders</h3>
        <ul>
          {orders.map(order => (
            <li key={order.id}>
              Order ID: {order.id} - Total: {order.total}
            </li>
          ))}
        </ul>
      </div>

      <div className='section'>
        <h3>Products</h3>
        <ul>
          {products.map(product => (
            <li key={product.id}>
              {product.name} - ${product.price} - Stock: {product.stock}
            </li>
          ))}
        </ul>
        <button onClick={toggleAddProductForm} className='btn btn-success'>
          {showAddProductForm ? 'Cancel' : 'Add Product'}
        </button>
        {showAddProductForm && (
          <div className='add-product-form'>
            <h4>Add New Product</h4>
            <form onSubmit={handleAddProduct}>
              <div className='form-group'>
                <label>Name:</label>
                <input
                  type='text'
                  name='name'
                  value={newProduct.name}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className='form-group'>
                <label>Price:</label>
                <input
                  type='number'
                  name='price'
                  value={newProduct.price}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className='form-group'>
                <label>Stock:</label>
                <input
                  type='number'
                  name='stock'
                  value={newProduct.stock}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className='form-group'>
                <label>Image URL:</label>
                <input
                  type='text'
                  name='image'
                  value={newProduct.image}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <button type='submit' className='btn btn-primary'>
                Add Product
              </button>
            </form>
          </div>
        )}
      </div>
    </div>
  );
}

export default AdminDashboard;
