import React, { useState, useEffect } from 'react';
import { useCart } from '../CartContext'; // Import useCart hook from CartContext
import './Home.css'; // Import your CSS file for styling

const Home = () => {
  const [products, setProducts] = useState([]);
  const { setCartItems } = useCart(); // Access setCartItems from CartContext

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch('http://127.0.0.1:5000/api/v1/products');
        const data = await response.json();
        setProducts(data.products);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  const handleAddToCart = (product) => {
    // Add product to cartItems in CartContext
    setCartItems((prevCartItems) => [...prevCartItems, product]);
    alert(`${product.name} added to cart!`);
  };

  return (
    <div className="home">
      <h1>Products</h1>
      <div className="product-list">
        {products.map((product) => (
          <div key={product.id} className="product-item">
            <h2>{product.name}</h2>
            <p>Price: ${product.price}</p>
            <p>Stock: {product.stock}</p>
            <div className="product-image">
              <img src={product.image} alt={product.name} />
              <button className="add-to-cart-button" onClick={() => handleAddToCart(product)}>
                Add to Cart
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
