import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Switch, Route, Redirect } from 'react-router-dom';
import Navbar from './Component/Navbar';
import Home from './Component/Pages/Home';
import CreateAccount from './Component/Pages/CreateAccount';
import Order from './Component/Pages/Order';
import Cart from './Component/Pages/Cart';
import AdminDashboard from './Component/Pages/adminDashboard'; // Corrected casing
import Login from './Component/Pages/login'; // Corrected casing
import Footer from './Component/Footer';
import { CartProvider } from './Component/CartContext'; // Adjust the path as per your project structure
import { AuthProvider } from './Component/auth'; // Add AuthProvider import
import SearchBar from './'; // Import SearchBar component
import './App.css';
import logo from './fierce__-removebg-preview.png';

function App() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    // Fetch products from your backend or use mock data
    // Replace the below line with actual API call
    const fetchProducts = async () => {
      const response = await fetch('/api/products'); // Adjust the URL as needed
      const data = await response.json();
      setProducts(data);
    };

    fetchProducts();
  }, []);

  // Assuming you have some way to differentiate admin and user roles
  const isAdmin = localStorage.getItem('isAdmin') === 'true';

  return (
    <AuthProvider> {/* Wrap entire application with AuthProvider */}
      <CartProvider> {/* Ensure CartProvider wraps the entire application */}
        <div className="App">
          <header className="App-header">
            <div className="left-section">
              <img src={logo} className="App-logo" alt="logo" />
            </div>

            <SearchBar products={products} /> {/* Use SearchBar component */}
          </header>

          <Router>
            <div> 
              <Navbar />
              <Switch>
                <Route path="/home">
                  <Home />
                </Route>
                <Route path="/cart">
                  <Cart />
                </Route>
                <Route path="/create-account">
                  <CreateAccount />
                </Route>
                <Route path="/order">
                  <Order />
                </Route>
                {isAdmin ? (
                  <>
                    <Route path="/admin-dashboard">
                      <AdminDashboard />
                    </Route>
                    <Redirect from="/" to="/admin-dashboard" />
                  </>
                ) : (
                  <Route path="/login">
                    <Login />
                  </Route>
                )}
                <Redirect from="/" to="/home" />
              </Switch>
              <Footer />
            </div>
          </Router>
        </div>
      </CartProvider>
    </AuthProvider>
  );
}

export default App;
