import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link, useHistory } from "react-router-dom";
import "./adminDashboard.css";

const AdminDashboard = () => {
  const [orders, setOrders] = useState([]);
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    name: "",
    price: "",
    stock: "",
    image: "",
  });
  const [error, setError] = useState("");
  const history = useHistory();

  useEffect(() => {
    fetchProducts();
    fetchOrders(); // Fetch orders when component mounts
  }, []);

  const fetchProducts = async () => {
    const token = localStorage.getItem("adminToken");
    try {
      const response = await axios.get(
        "http://127.0.0.1:5000/api/v1/admin/products",
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setProducts(response.data.products || []);
    } catch (error) {
      console.error("Error fetching products:", error);
      setError("Failed to fetch products");
    }
  };

  const fetchOrders = async () => {
    const token = localStorage.getItem("adminToken");
    try {
      const response = await axios.get(
        "http://127.0.0.1:5000/api/v1/admin/orders",
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setOrders(response.data.orders || []);
    } catch (error) {
      console.error("Error fetching orders:", error);
      setError("Failed to fetch orders");
    }
  };

  const handleAddProduct = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("adminToken");
    try {
      await axios.post(
        "http://127.0.0.1:5000/api/v1/admin/products",
        newProduct,
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        }
      );
      fetchProducts(); // Refresh products list after adding
      setNewProduct({
        name: "",
        price: "",
        stock: "",
        image: "",
      });
      setError(""); // Clear any previous errors
    } catch (error) {
      console.error("Error adding product:", error);
      setError("Failed to add product");
    }
  };

  const handleApproveOrder = async (orderId) => {
    const token = localStorage.getItem("adminToken");
    try {
      await axios.put(
        `http://127.0.0.1:5000/api/v1/admin/orders/${orderId}/approve`,
        {},
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      fetchOrders(); // Refresh orders list after approval
    } catch (error) {
      console.error("Error approving order:", error);
      setError("Failed to approve order");
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct((prevProduct) => ({
      ...prevProduct,
      [name]: value,
    }));
  };

  const handleLogout = () => {
    localStorage.removeItem("adminToken");
    history.push("/login");
  };

  return (
    <div className="admin-dashboard">
      <h2>Admin Dashboard</h2>
      <button onClick={handleLogout} className="logout-button">Logout</button>
      {error && <div className="error-message">{error}</div>}

      <section>
        <h3>Orders</h3>
        <table className="orders-table">
          <thead>
            <tr>
              <th>Order ID</th>
              <th>Customer</th>
              <th>Total</th>
              <th>Status</th>
              <th>Date</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {orders.length > 0 ? (
              orders.map((order) => (
                <tr key={order.id}>
                  <td>{order.id}</td>
                  <td>{order.customer_name}</td>
                  <td>{order.total}</td>
                  <td>{order.status}</td>
                  <td>{new Date(order.order_date).toLocaleString()}</td>
                  <td>
                    <button
                      onClick={() => handleApproveOrder(order.id)}
                      className="btn btn-approve"
                    >
                      Approve
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="6">No orders to display</td>
              </tr>
            )}
          </tbody>
        </table>
      </section>

      <section>
        <h3>Products</h3>
        <table className="products-table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Price</th>
              <th>Stock</th>
            </tr>
          </thead>
          <tbody>
            {products.length > 0 ? (
              products.map((product) => (
                <tr key={product.id}>
                  <td>{product.name}</td>
                  <td>{product.price}</td>
                  <td>{product.stock}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="3">No products to display</td>
              </tr>
            )}
          </tbody>
        </table>
      </section>

      <section>
        <h3>Add Product</h3>
        <form onSubmit={handleAddProduct} className="add-product-form">
          <div className="form-group">
            <label>Name:</label>
            <input
              type="text"
              name="name"
              value={newProduct.name}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Price:</label>
            <input
              type="number"
              name="price"
              value={newProduct.price}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Stock:</label>
            <input
              type="number"
              name="stock"
              value={newProduct.stock}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Image URL:</label>
            <input
              type="text"
              name="image"
              value={newProduct.image}
              onChange={handleInputChange}
              required
            />
          </div>
          <button type="submit" className="btn btn-success">Add Product</button>
        </form>
      </section>
    </div>
  );
};

export default AdminDashboard;
