import React, { useEffect, useState } from 'react';
import axios from 'axios'; // Using axios for API calls

const AdminDashboard = ({ products }) => {
  const [orders, setOrders] = useState([]);
  const adminToken = localStorage.getItem('adminToken');

  // Fetch orders from API when the component mounts
  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:5000/api/v1/admin/orders', {
          headers: {
            Authorization: `Bearer ${adminToken}`, // Add token in the headers
          },
        });
        setOrders(response.data.orders || []); // Ensure orders is always an array
      } catch (error) {
        console.error('Error fetching orders:', error);
      }
    };

    if (adminToken) {
      fetchOrders(); // Only fetch orders if adminToken exists
    }
  }, [adminToken]);

  return (
    <div className="admin-dashboard">
      <h1>Admin Dashboard</h1>
      
      <section className="products-section">
        <h2>Products</h2>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Price</th>
              <th>Stock</th>
            </tr>
          </thead>
          <tbody>
            {products.length > 0 ? (
              products.map((product) => (
                <tr key={product.id}>
                  <td>{product.id}</td>
                  <td>{product.name}</td>
                  <td>{product.price}</td>
                  <td>{product.stock}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="4">No products available</td>
              </tr>
            )}
          </tbody>
        </table>
      </section>

      <section className="orders-section">
        <h2>Orders</h2>
        <table>
          <thead>
            <tr>
              <th>Order ID</th>
              <th>Customer</th>
              <th>Total</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {orders.length > 0 ? (
              orders.map((order) => (
                <tr key={order.id}>
                  <td>{order.id}</td>
                  <td>{order.customer_name}</td>
                  <td>{order.total}</td>
                  <td>{order.status}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="4">No orders available</td>
              </tr>
            )}
          </tbody>
        </table>
      </section>
    </div>
  );
};

export default AdminDashboard;
