import React from 'react';
import { useCart } from '../CartContext'; // Import useCart hook from CartContext
import './Cart.css';

const Cart = () => {
  const { cartItems, setCartItems } = useCart(); // Access cartItems and setCartItems from CartContext

  const totalCost = cartItems.reduce((sum, item) => sum + item.price, 0);

  const handleOrder = async () => {
    const orderData = {
      items: cartItems.map(item => ({
        productId: item.id,
        quantity: 1, // Adjust quantity as needed
        price: item.price,
      })),
    };

    try {
      const response = await fetch('http://127.0.0.1:5000/api/v1/order', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(orderData),
      });

      const data = await response.json();

      if (response.ok) {
        console.log('Order placed:', data);
        setCartItems([]); // Clear the cart after successful order
      } else {
        console.error('Order error:', data.message || 'Failed to place order');
      }
    } catch (error) {
      console.error('Order error:', error);
    }
  };

  return (
    <div className="cart">
      <h1>Your Cart</h1>
      <div className="cart-items">
        {cartItems.length ? (
          cartItems.map((item) => (
            <div key={item.id} className="cart-item">
              <span>{item.name}</span>
              <span>Price: ${item.price}</span>
            </div>
          ))
        ) : (
          <div>No items in the cart</div>
        )}
      </div>
      {cartItems.length > 0 && (
        <div>
          <div className="total-cost">
            <strong>Total Cost: ${totalCost.toFixed(2)}</strong>
          </div>
          <button className="order-button" onClick={handleOrder}>
            Place Order
          </button>
        </div>
      )}
    </div>
  );
};

export default Cart;
