// Cart.js
import React from 'react';
import { useCart } from '../CartContext';

const Cart = () => {
  const { cartItems, totalPrice } = useCart();

  const createOrder = async () => {
    // Collect client details (dummy data for illustration)
    const clientDetails = {
      name: 'John Doe',
      address: '123 Main St, Anytown'
      // Add more client details as needed
    };

    // Prepare order data
    const orderData = {
      client: clientDetails,
      items: cartItems,
      totalPrice: totalPrice
    };

    try {
      // Send order data to backend API
      const response = await fetch('http://127.0.0.1:5000/api/v1/orders', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(orderData)
      });

      if (response.ok) {
        // Order successfully created
        console.log('Order created successfully.');
        
        // Clear cart items or update state as needed
      } else {
        // Handle error response
        console.error('Failed to create order:', response.statusText);
      }
    } catch (error) {
      console.error('Error creating order:', error);
    }
  };

  return (
    <div className="cart">
      <h2>Cart</h2>
      <ul>
        {cartItems.map((item, index) => (
          <li key={index}>{item.name} - ${item.price}</li>
        ))}
      </ul>
      <p>Total Price: ${totalPrice}</p>
      <button onClick={createOrder}>Order</button>
    </div>
  );
};

export default Cart;
