// App.js
import React from 'react';
import { BrowserRouter as Router, Switch, Route, Redirect } from 'react-router-dom';
import Navbar from './Component/Navbar';
import Home from './Component/Pages/Home';
import CreateAccount from './Component/Pages/CreateAccount';
import Order from './Component/Pages/Order';
import Cart from './Component/Pages/Cart';
import AdminDashboard from './Component/Pages/AdminDashboard'; // Corrected casing
import Login from './Component/Pages/'; // Corrected casing
import Footer from './Component/Footer';
import { CartProvider } from './Component/CartContext'; // Adjust the path as per your project structure
import { AuthProvider } from './Component/AuthContext'; // Add AuthProvider import
import './App.css';
import logo from './fierce__-removebg-preview.png';

function App() {
  const handleSearch = () => {
    console.log('Perform search');
  };

  // Assuming you have some way to differentiate admin and user roles
  const isAdmin = localStorage.getItem('isAdmin') === 'true';

  return (
    <AuthProvider> {/* Wrap entire application with AuthProvider */}
      <CartProvider> {/* Ensure CartProvider wraps the entire application */}
        <div className="App">
          <header className="App-header">
            <div className="left-section">
              <img src={logo} className="App-logo" alt="logo" />
            </div>

            <div className="search-bar">
              <input className="active" type="text" placeholder="Search..." />
              <button onClick={handleSearch}>Search</button>
            </div>
          </header>

          <Router>
            <div> 
              <Navbar />
              <Switch>
                <Route path="/home">
                  <Home />
                </Route>
                <Route path="/cart">
                  <Cart />
                </Route>
                <Route path="/create-account">
                  <CreateAccount />
                </Route>
                <Route path="/order">
                  <Order />
                </Route>
                {isAdmin ? (
                  <>
                    <Route path="/admin-dashboard">
                      <AdminDashboard />
                    </Route>
                    <Redirect from="/" to="/admin-dashboard" />
                  </>
                ) : (
                  <Route path="/login">
                    <Login />
                  </Route>
                )}
                <Redirect from="/" to="/home" />
              </Switch>
              <Footer />
            </div>
          </Router>
        </div>
      </CartProvider>
    </AuthProvider>
  );
}

export default App;
