import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import "./adminDashboard.css";

const AdminDashboard = () => {
  const [orders, setOrders] = useState([]);
  const [error, setError] = useState("");
  const history = useHistory();

  useEffect(() => {
    fetchOrders(); // Fetch orders when component mounts
  }, []);

  // Fetch Orders
  const fetchOrders = async () => {
    const token = localStorage.getItem("adminToken");

    try {
      const response = await fetch("http://127.0.0.1:5000/api/v1/admin/orders", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (response.ok) {
        const data = await response.json();
        setOrders(data.orders || []);
      } else {
        console.error("Failed to fetch orders:", response.statusText);
        setError("Failed to fetch orders");
      }
    } catch (error) {
      console.error("Error fetching orders:", error);
      setError("Failed to fetch orders");
    }
  };

  // Approve Order
  const handleApproveOrder = async (orderId) => {
    const token = localStorage.getItem("adminToken");

    try {
      await fetch(`http://127.0.0.1:5000/api/v1/admin/orders/${orderId}/approve`, {
        method: "PUT",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      fetchOrders(); // Refresh orders list after approval
    } catch (error) {
      console.error("Error approving order:", error);
      setError("Failed to approve order");
    }
  };

  // Logout functionality
  const handleLogout = () => {
    localStorage.removeItem("adminToken");
    history.push("/login");
  };

  return (
    <div className="admin-dashboard">
      <h2>Admin Dashboard</h2>
      <button onClick={handleLogout} className="logout-button">
        Logout
      </button>
      {error && <div className="error-message">{error}</div>}

      <section>
        <h3>Orders</h3>
        <table className="orders-table">
          <thead>
            <tr>
              <th>Order ID</th>
              <th>Customer</th>
              <th>Total</th>
              <th>Status</th>
              <th>Date</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {orders.length > 0 ? (
              orders.map((order) => (
                <tr key={order.id}>
                  <td>{order.id}</td>
                  <td>{order.client.name}</td>
                  <td>{order.totalPrice}</td>
                  <td>{order.status}</td>
                  <td>{new Date(order.order_date).toLocaleString()}</td>
                  <td>
                    <button
                      onClick={() => handleApproveOrder(order.id)}
                      className="btn btn-approve"
                    >
                      Approve
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="6">No orders to display</td>
              </tr>
            )}
          </tbody>
        </table>
      </section>
    </div>
  );
};

export default AdminDashboard;
