import React, { useState } from 'react';
import { useHistory, Link } from 'react-router-dom';
import './CreateAccount.css';

const CreateAccount = () => {
  const [form, setForm] = useState({
    name: '',
    email: '',
    password: '',
    phone_number: '',
    address: ''
  });

  const [message, setMessage] = useState('');
  const history = useHistory();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({
      ...form,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://127.0.0.1:5000/api/v1/user/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          ...form,
          role: 'customer' // Set default role to customer
        }),
      });
      const data = await response.json();

      if (response.ok) {
        setMessage('Account created successfully!');
        localStorage.setItem('token', data.token);
        
        // Check if admin login is required
        if (data.isAdmin) {
          history.push('/admin-dashboard'); // Redirect to admin dashboard
        } else {
          history.push('/products'); // Redirect to products page after successful registration
        }
      } else {
        setMessage(data.message || 'Failed to create account');
      }
    } catch (error) {
      console.error('Account creation error:', error);
      setMessage('An error occurred. Please try again.');
    }
  };

  return (
    <div className="outer-container">
      <div className="inner-box create-account">
        <h2>Create Account</h2>
        {message && <p>{message}</p>}
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="name">Name:</label>
            <input type="text" id="name" name="name" value={form.name} onChange={handleChange} required />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email:</label>
            <input type="email" id="email" name="email" value={form.email} onChange={handleChange} required />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input type="password" id="password" name="password" value={form.password} onChange={handleChange} required />
          </div>
          <div className="form-group">
            <label htmlFor="phone_number">Phone Number:</label>
            <input type="text" id="phone_number" name="phone_number" value={form.phone_number} onChange={handleChange} />
          </div>
          <div className="form-group">
            <label htmlFor="address">Address:</label>
            <textarea id="address" name="address" value={form.address} onChange={handleChange}></textarea>
          </div>
          <button type="submit">Create Account</button>
        </form>
        <p>Already have an account? <Link to="/login">Login</Link></p>
        {/* Admin login link */}
        <p>Admin? <Link to="/admin/login">Admin Login</Link></p>
      </div>
    </div>
  );
};

export default CreateAccount;
